import React, { Component, useEffect, useState } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import { filter, includes, orderBy as funcOrderBy } from "lodash";
import { v4 as uuidv4 } from "uuid";

function App(props) {
  const [items, setItems] = useState([]);
  const [itemSelected, setItemSelected] = useState(null);
  const [isShowForm, setIsShowForm] = useState(false);
  const [strSearch, setStrSearch] = useState("");
  const [orderBy, setOrderBy] = useState("name");
  const [orderDir, setOrderDir] = useState("asc");

  useEffect(() => {
    let items = JSON.parse(localStorage.getItem("task"));
    setItems(items);
  }, []);

  function handleEdit(item) {
    setIsShowForm(true);
    setItemSelected(item);
  }

  function handleDelete(id) {
    let newData = items.filter((item) => item.id !== id);
    setItems({
      ...items,
      newData,
    });
    localStorage.setItem("task", JSON.stringify(newData));
  }
  function handleSort(orderBy, orderDir) {
    setOrderBy(orderBy);
    setOrderDir(orderDir);
  }

  function handleAddItem(item) {
    let newItemsAdd = [];
    if (item.id !== "") {
      items.forEach((elm, key) => {
        if (elm.id === item.id) {
          items[key].name = item.name;
          items[key].level = +item.level;
        }
      });
    } else {
      let newItem = {
        ...item,
        id: uuidv4(),
        level: +item.level,
      };
      newItemsAdd = [...items, newItem];
    }

    setItems(newItemsAdd);
    setIsShowForm(false);

    localStorage.setItem("task", JSON.stringify(newItemsAdd));
  }

  let itemsFilter = [];
  // Search C치ch 1
  itemsFilter = filter(items, (item) => {
    return includes(item.name.toLowerCase(), strSearch.toLowerCase());
  });

  // Search C치ch 2
