import React, { Component } from "react";

class Form extends Component {
  constructor(props) {
    super(props);

    this.state = {
      task_id: "",
      task_name: "",
      task_level: 0,
    };

    // this.handleCancel = this.handleCancel.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentWillMount() {
    this.updateItem(this.props.itemSelected);
  }

  componentWillReceiveProps(nextProps) {
    this.updateItem(nextProps.itemSelected);
  }

  updateItem(item) {
    if (item !== null) {
      this.setState({
        task_id: item.id,
        task_name: item.name,
        task_level: item.level,
      });
    }
  }

  handleChange(event) {
    const target = event.target; // input và select
    const value = target.value;
    const name = target.name;
    console.log(name);

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    let item = {
      id: this.state.task_id,
      name: this.state.task_name,
      level: this.state.task_level,
    };

    this.props.onClickAddItem(item);
    this.setState({
      task_name: "",
      task_level: 0,
    });
  }

  handleCancel() {
    this.props.onClickCancel();
  }

  render() {
    return (
      <div className="row">
        <div className="col-md-offset-7 col-md-5">
          <form className="form-inline" onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label className="sr-only">label</label>
              <input
                value={this.state.task_name}
                onChange={this.handleChange}
                name="task_name"
                type="text"
                className="form-control"
                placeholder="Task Name"
              />
            </div>
            <div className="form-group">
              <label className="sr-only">label</label>
              <select
                value={this.state.task_level}
                onChange={this.handleChange}
                name="task_level"
                id="inputDs"
                className="form-control"
                required="required"
              >
                Small
                <option value={0}>Small</option>
                <option value={1}>Medium</option>
                <option value={2}>High</option>
              </select>
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
            <button onClick={this.handleCancel} type="button" className="btn btn-default">
              Cancel
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default Form;
