import React, { Component } from "react";

class Item extends Component {
  constructor(props) {
    super(props);

    this.state = {};
    this.handleDelete = this.handleDelete.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  showElementLevel(level) {
    let elmLevel = "";
    if (level === 0) {
      elmLevel = <span className="label label-default">Small</span>;
    } else if (level === 1) {
      elmLevel = <span className="label label-info">Medium</span>;
    } else if (level === 2) {
      elmLevel = <span className="label label-danger">High</span>;
    }
    return elmLevel;
  }

  handleDelete(id) {
    this.props.onClickDelete(id);
  }

  handleEdit(item) {
    this.props.onClickEdit(item);
  }

  render() {
    let item = this.props.item;
    let index = this.props.index;

    return (
      <tr>
        <td className="text-center">{index + 1}</td>
        <td>{item.name}</td>
        <td className="text-center">{this.showElementLevel(item.level)}</td>
        <td>
          <button onClick={() => this.handleEdit(item)} type="button" className="btn btn-warning">
            Edit
          </button>
          <button onClick={() => this.handleDelete(item.id)} type="button" className="btn btn-danger">
            Delete
          </button>
        </td>
      </tr>
    );
  }
}

export default Item;
