import React, { Component } from "react";
import Search from "./Search";
import Sort from "./Sort";

class Control extends Component {
  constructor(props) {
    super(props);

    this.state = {};
    this.handleAdd = this.handleAdd.bind(this);
  }

  handleAdd() {
    this.props.onClickAdd();
  }

  render() {
    let elmButton = "";
    if (this.props.isShowForm === false) {
      elmButton = (
        <button onClick={this.handleAdd} type="button" className="btn btn-info btn-block">
          Add Task
        </button>
      );
    } else {
      elmButton = (
        <button onClick={this.handleAdd} type="button" className="btn btn-success btn-block">
          Close Form
        </button>
      );
    }

    let orderBy = this.props.orderBy;
    let orderDir = this.props.orderDir;
    let onClickSort = this.props.onClickSort;
    return (
      <div className="row">
        <Search onClickGo={this.props.onClickSearchGo}></Search>
        <Sort onClickSort={onClickSort} orderBy={orderBy} orderDir={orderDir}></Sort>
        <div className="col-xs-5 col-sm-5 col-md-5 col-lg-5">{elmButton}</div>
      </div>
    );
  }
}

export default Control;
