import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import { filter, includes, orderBy as funcOrderBy } from "lodash";
import { v4 as uuidv4 } from "uuid";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      itemSelected: null,
      isShowForm: false,
      strSearch: "",
      orderBy: "name",
      orderDir: "asc",
    };

    // this.handleToggleForm = this.handleToggleForm.bind(this);
    this.closeForm = this.closeForm.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    // this.handleSort = this.handleSort.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.handleAddItem = this.handleAddItem.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  componentWillMount() {
    let items = JSON.parse(localStorage.getItem("task"));
    this.setState({
      items: items,
    });

    // // set
    // localStorage.setItem("tasks", "ds");

    // //get
    // console.log(localStorage.getItem("tasks"));

    // //remove
    // localStorage.removeItem("tasks");

    // //get
    // console.log(localStorage.getItem("tasks"));
  }

  handleToggleForm = () => {
    this.setState({
      isShowForm: !this.state.isShowForm,
      itemSelected: null,
    });
  };

  closeForm() {
    this.setState({
      isShowForm: false,
    });
  }

  handleSearch(value) {
    this.setState({
      strSearch: value,
    });
  }

  handleSort = (orderBy, orderDir) => {
    this.setState({
      orderBy: orderBy,
      orderDir: orderDir,
    });
  };
  handleDelete(id) {
    let items = this.state.items;
    let newData = items.filter((item) => item.id !== id);
    this.setState({
      items: newData,
    });
    localStorage.setItem("task", JSON.stringify(newData));
  }

  handleAddItem(item) {
    let items = this.state.items;
    if (item.id !== "") {
      items.forEach((elm, key) => {
        if (elm.id === item.id) {
          items[key].name = item.name;
          items[key].level = +item.level;
        }
      });
    } else {
      let newItem = {
        ...item,
        id: uuidv4(),
        level: +item.level,
      };
      items = [...this.state.items, newItem];
    }
    this.setState({
      items: items,
      isShowForm: false,
    });
    localStorage.setItem("task", JSON.stringify(items));
  }

  handleEdit(item) {
    this.setState({
      itemSelected: item,
      isShowForm: true,
    });
  }

  render() {
    let itemsOrigin = [...this.state.items];
    let items = [];
    let { orderBy, orderDir, isShowForm, strSearch, itemSelected } = this.state;

    // Search C치ch 1
    items = filter(itemsOrigin, (item) => {
      return includes(item.name.toLowerCase(), strSearch.toLowerCase());
    });
    // Search C치ch 2
    // itemsOrigin.forEach((item) => {
    //   if (item.name.toLowerCase().indexOf(search.toLowerCase()) !== -1) {
    //     items.push(item);
    //   }
    // });

    // Sort
    items = funcOrderBy(items, [orderBy], [orderDir]);

    let elmForm = null;
    if (isShowForm) {
      elmForm = (
        <Form onClickCancel={this.closeForm} onClickAddItem={this.handleAddItem} itemSelected={itemSelected}></Form>
      );
    }

    let x = 100;
    let y = x++;
    let z = ++x;

    let n = x == y ? z++ : ++z;
    console.log(n);

    return (
      <div>
        <div className="container">
          <Title></Title>
          <Control
            orderBy={orderBy}
            orderDir={orderDir}
            onClickSearchGo={this.handleSearch}
            isShowForm={isShowForm}
            onClickAdd={this.handleToggleForm}
            onClickSort={this.handleSort}
          ></Control>
          {elmForm}
          <List onClickEdit={this.handleEdit} items={items} onClickDelete={this.handleDelete}></List>
        </div>
      </div>
    );
  }
}

export default App;
