import React, { Component } from "react";

class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      strSearch: "",
    };
    this.handleSearch = this.handleSearch.bind(this);
    this.handleClear = this.handleClear.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleSearch() {
    console.log(this.state.strSearch);
    this.props.onClickGo(this.state.strSearch);
  }

  handleClear() {
    this.setState({
      strSearch: "",
    });
    this.props.onClickGo("");
  }

  handleChange(e) {
    this.setState({
      strSearch: e.target.value,
    });
  }

  render() {
    return (
      <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
        <div className="input-group">
          <input
            value={this.state.strSearch}
            onChange={this.handleChange}
            type="text"
            className="form-control"
            placeholder="Search for..."
          />
          <span className="input-group-btn">
            <button onClick={this.handleSearch} className="btn btn-info" type="button">
              Go
            </button>
            <button onClick={this.handleClear} className="btn btn-warning" type="button">
              Clear
            </button>
          </span>
        </div>
      </div>
    );
  }
}

export default Search;
